package javabasic.Basic;

public class assenment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a = 34;
		float b = 78;
		
		a+= 6;
		System.out.println(a);
		
		b-=4;
		System.out.println(b);
		
		a*= 5;
		System.out.println(a);
		
		b/=7;
		System.out.println(b);
		
		a%=9;
		System.out.println(a);

	}

}
