package javabasic.Basic;

public class logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//  &&and,||or,!not
		
	int a = 45;
	int b = 67;
	int c = 78;
	
	System.out.println(a>b&&a>c);
	System.out.println(a<b||a>c);
	System.out.println(!(a>b));	 
	
		
		
	}

}
